import React, { Component } from 'react';
import { connect } from 'react-redux'
import './App.css';
// Redux
// Action constants
const GET_QUOTE = 'GET_QUOTE'
// style
const color = 'red'
const appStyle = {
  backgroundColor: color,
  height: '100vh'
}
const textBoxStyle = {
  position: 'absolute',
  backgroundColor: 'white',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%,0)',
  padding: '20px',
  width: '40vw'
}
const textStyle = {
  color: color,
}
const authorStyle = {
  color: color,
  textAlign: 'right'
}
const leftButtonStyle = {
  borderRadius: '5px 5px 5px 5px',
  fontSize: '20px',
  marginTop: '10px',
  color: 'white',
  backgroundColor: color,
  float: 'left'
}
const rightButtonStyle = {
  borderRadius: '5px 5px 5px 5px',
  padding: '10px',
  marginTop: '10px',
  color: 'white',
  backgroundColor: color,
  float: 'right'
}
class App extends Component {
  render() {
    return (
      <div style={appStyle}>
        <div id="quote-box" style={textBoxStyle}>
          <div id="text" style={textStyle}>
            {this.props.quotes[0].content}
          </div>
          <div id="author" style={authorStyle}>-{this.props.quotes[0].author}</div>
          <div>
            <button id="tweet-quote" style={leftButtonStyle}><i class="fab fa-twitter-square"></i></button>
            <button id="new-quote" style={rightButtonStyle}>New Quote</button>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    quotes: state
  }
}
const mapDispatchToProps = (dispatch) => {
  return {
    getQuotes: () => dispatch({ type: GET_QUOTE })
  }
}
export default connect(mapStateToProps, mapDispatchToProps)(App);
