import React, { Component } from 'react';
import { connect } from 'react-redux'
import './App.css';
// Redux
// Action constants
const GET_QUOTE = 'GET_QUOTE'
// style
let color = 'rgb(44, 62, 80)'
let appStyle = {
  backgroundColor: color,
  height: '100vh',
  borderRadius: '10px 10px 10px 10px'
}
const textBoxStyle = {
  position: 'absolute',
  backgroundColor: 'white',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%,0)',
  padding: '20px',
  width: '40vw'
}
let textStyle = {
  color: color,
}
let authorStyle = {
  color: color,
  textAlign: 'right'
}
let leftButtonStyle = {
  borderRadius: '5px 5px 5px 5px',
  fontSize: '20px',
  marginTop: '10px',
  color: 'white',
  backgroundColor: color,
  float: 'left'
}
let rightButtonStyle = {
  borderRadius: '5px 5px 5px 5px',
  padding: '10px',
  marginTop: '10px',
  color: 'white',
  backgroundColor: color,
  float: 'right'
}

class App extends Component {
  constructor(props) {
    super(props)
  }
  componentDidMount() {
    this.props.getQuotes()
  }
  onGetNewQuote = () => {
    this.props.getQuotes()
    color =
      'rgb(' + Math.floor(Math.random() * 256) + ',' + Math.floor(Math.random() * 256) + ',' + Math.floor(Math.random() * 256) + ')'
    appStyle.backgroundColor = color
    textStyle.color = color
    authorStyle.color = color
    leftButtonStyle.backgroundColor = color
    rightButtonStyle.color = color
    alert(JSON.stringify(rightButtonStyle))
  }
  render() {

    return (
      <div style={appStyle}>
        <div id="quote-box" style={textBoxStyle}>
          <div id="text" style={textStyle}>
            {this.props.quote.content}
          </div>
          <div id="author" style={authorStyle}>-{this.props.quote.author}</div>
          <div>
            <p><a id="tweet-quote" target="_blank" href="twitter.com/intent/tweet" style={leftButtonStyle}><i class="fab fa-twitter-square"></i></a></p>
            <button id="new-quote" style={rightButtonStyle} onClick={this.onGetNewQuote}>New Quote</button>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    quote: state
  }
}
const mapDispatchToProps = (dispatch) => {
  return {
    getQuotes: () => { dispatch({ type: GET_QUOTE }) }
  }
}
export default connect(mapStateToProps, mapDispatchToProps)(App);
